import {getQuestions} from '../fonctions/appelleApi';
import Question from './Question';
import Reponse from './Reponse'

class AppQuestion extends Component{
    constructor(props){
        super(props)
        this.state={
            listeQuestions:[],
            etape:null
        }
    }

    componentDidMount() {
        this.initQuestions()
    }

    initQuestions = () => {
        getQuestions(10).then(data =>{
            this.setState(
                {
                    listeQuestions:data,
                    etape:0
                }
            )
            console.log(this.state.listeQuestions)
        })
    }

