            })
        }
    }


    render(){
        let {listeQuestions, etape} = this.state;
        console.log(listeQuestions.length)
        console.log(etape)
        if(listeQuestions.length>0){
            return (
                <div>
                    {etape <= listeQuestions.length ?
                        (<>
                        <Question enonce={listeQuestions[etape].enonce}/>
                        <Reponse 
                            reponse={listeQuestions[etape].reponse}
                            etape={etape}
                            verifReponse={this.verifReponse()}
                            reponseCorrect={null}
                            reponseCliquer={null}
                        />
                        </>):(
                            <div>
                                <h1>Quiz termin√©</h1>
                                <p>Merci !</p>
                            </div>
                        )
                    }
                </div>
            )
        }else{
            return (null)
        }
    }
}

export default AppQuestion
