import React from 'react';

const Reponse = (props) => {
    let nbReponse = 0
    let reponses
    for(let i=0;i<props.reponses.length;i++){
        reponses=
            <li
            onClick={() => {
                props.verifReponse(nbReponse)
                nbReponse++           
            }}
            key={props.reponses[nbReponse]}>
                {props.reponses[nbReponse]}
            </li>
        nbReponse++
    }
    console.log(nbReponse)

    return (
        <>
            <ul disabled={props.reponseCliquer ? true : false}>
                {reponses}
            </ul>
            <div>
                {
                    props.reponseCorrect ? 'Bonne réponse !' : props.reponseCliquer ? 'Mauvaise réponse!' : ''
                }
            </div>
        </>
    );
}

export default Reponse;
