import React from 'react';

const Reponse = (props) => {
    let nbReponse = 0
    let reponses
    for(let i=0;i<props.reponses.length;i++){
        reponses=
            <li
            onClick={() => {
                props.verifReponse(nbReponse)
                nbReponse++           
            }}
            key={props.reponses[nbReponse]}>
                {props.reponses[nbReponse]}
            </li>
        nbReponse++
    }
    reponses = props.reponses
        .map((reponse, i) => (
            <div class="reponse"
            onClick={() => {
                props.verifReponse(i)          
            }}
            key={reponse}>
                {reponse}
            </div>
        ));

    return (
        <>
            <div id="reponses">
                {reponses}
            </div>
            <div>
                {
                    props.reponseCorrect ? 'Bonne réponse !' : props.reponseCliquer ? 'Mauvaise réponse!' : ''
                }
            </div>
        </>
    );
}

export default Reponse;
