import React from 'react';

const Reponse = (props) => {
    let reponses = Object.keys(props.reponse)
        .map((qAnswer, i) => (
            <li
            className=
            {
                props.correctAnswer === qAnswer ?
                'correct' : 
                props.clickedAnswer === qAnswer ? 
                'incorrect' : ''
            }
            onClick={() => props.verifReponse(qAnswer)}
            key={qAnswer}>
                {props.answer[qAnswer]}
            </li>
        ));

    return (
        <>
            <ul disabled={props.clickedAnswer ? true : false} className="Answers">
                {reponses}
            </ul>
            <div>
                {
                    props.correctAnswer ? 'Bonne réponse !' : props.clickedAnswer ? 'Mauvaise réponse!' : ''
                }
            </div>
        </>
    );
}

export default Reponse;
