import React, {Component} from 'react';
import {getQuestions} from '../fonctions/appelleApi';

class AppQuestion extends Component{
    constructor(props){
        super(props)
        this.state={
            listeQuestions:[],
            etape:null
        }

    }

    componentDidMount() {
        this.initQuestions()
    }
    
    componentWillUnmount() {
    }

    initQuestions = () => {
        getQuestions(10).then(data =>{
            this.setState(
                {
                    listeQuestions:data,
                    etape:0
                }
            )
            console.log(this.state.listeQuestions)
        })
    }

    renderQuestion = () => {
        let {listeQuestions} = this.state;
        return (
            <h1>
                {listeQuestions.enonce}
            </h1>
            )
      }

    render(){
        let {listeQuestions} = this.state;
        return (
            <div>
                {step <= listeQuestions.length ?
                    (
                        renderQuestion()
                    ):(
                        <div>
                            <h1>Quiz termin√©</h1>
                            <p>Merci !</p>
                        </div>
                    )
                }
            </div>
        )
    }
}

export default AppQuestion
