import React from 'react';

const Reponse = (props) => {
    console.log(props.reponses)
    let reponses = props.reponses
        .map((reponse) => (
            
            <li
            onClick={() => props.verifReponse(reponse)}
            key={reponse}>
                {reponse}
                {console.log(reponse)}
            </li>
        ));

    return (
        <>
        {console.log(reponses)}
            <ul disabled={props.clickedAnswer ? true : false} className="Answers">
                {reponses}
            </ul>
            <div>
                {
                    props.correctAnswer ? 'Bonne réponse !' : props.clickedAnswer ? 'Mauvaise réponse!' : ''
                }
            </div>
        </>
    );
}

export default Reponse;
