import React, {Component} from 'react';
import {getQuestions} from '../fonctions/appelleApi';
import Question from './Question';

class AppQuestion extends Component{
    constructor(props){
        super(props)
        this.state={
            listeQuestions:[],
            etape:null
        }

    }

    componentDidMount() {
        this.initQuestions()
    }
    
    componentWillUnmount() {
    }

    initQuestions = () => {
        getQuestions(10).then(data =>{
            this.setState(
                {
                    listeQuestions:data,
                    etape:0
                }
            )
            console.log(this.state.listeQuestions)
        })
    }


    render(){
        let {listeQuestions, etape} = this.state;
        console.log(listeQuestions.length)
        console.log(etape)
        if(listeQuestions!=null)
        return (
            <div>
                {etape <= listeQuestions.length ?
                    (<>
                    <Question enonce={listeQuestions[etape].enonce}/>
                    </>):(
                        <div>
                            <h1>Quiz termin√©</h1>
                            <p>Merci !</p>
                        </div>
                    )
                }
            </div>
        )
    }
}

export default AppQuestion
