import React from 'react';

const Reponse = (props) => {
    let nbReponse = 0
    for(i=0;i<props.reponses.length;i++){
        nbReponse++
    }
    console.log(nbReponse)
    let reponses = props.reponses
        .map((reponse) => (
            <li
            onClick={() => {
                props.verifReponse(nbReponse)
                nbReponse++           
            }}
            key={reponse}>
                {reponse}
            </li>
        ));

    return (
        <>
            <ul disabled={props.reponseCliquer ? true : false}>
                {reponses}
            </ul>
            <div>
                {
                    props.reponseCorrect ? 'Bonne réponse !' : props.reponseCliquer ? 'Mauvaise réponse!' : ''
                }
            </div>
        </>
    );
}

export default Reponse;
